import React from 'react';
import { Box, Container, Typography, Grid, Paper, LinearProgress } from '@mui/material';
import { motion } from 'framer-motion';

interface Skill {
  name: string;
  level: number;
  color: string;
}

const skills: Skill[] = [
  { name: 'PHP', level: 90, color: '#777BB4' },
  { name: 'MySQL', level: 85, color: '#4479A1' },
  { name: 'HTML/CSS', level: 80, color: '#E34F26' },
  { name: 'JavaScript', level: 75, color: '#F7DF1E' },
  { name: 'AJAX', level: 70, color: '#00758F' },
  { name: 'Git & Github', level: 75, color: '#F05032' },
  { name: 'C/C++', level: 70, color: '#00599C' },
];

const Skills: React.FC = () => {
  return (
    <Box
      id="skills"
      sx={{
        py: 8,
        backgroundColor: '#fff',
      }}
    >
      <Container>
        <motion.div
          initial={{ opacity: 0 }}
          whileInView={{ opacity: 1 }}
          viewport={{ once: true }}
          transition={{ duration: 1 }}
        >
          <Typography variant="h3" component="h2" gutterBottom align="center" color="primary">
            Skills
          </Typography>

          <Grid container spacing={4} sx={{ mt: 2 }}>
            <Grid item xs={12}>
              <Paper elevation={3} sx={{ p: 4 }}>
                {skills.map((skill, index) => (
                  <Box key={skill.name} sx={{ mb: 3 }}>
                    <motion.div
                      initial={{ opacity: 0, x: -20 }}
                      whileInView={{ opacity: 1, x: 0 }}
                      transition={{ delay: index * 0.1 }}
                    >
                      <Typography variant="h6" gutterBottom>
                        {skill.name}
                      </Typography>
                      <LinearProgress
                        variant="determinate"
                        value={skill.level}
                        sx={{
                          height: 10,
                          borderRadius: 5,
                          backgroundColor: '#eee',
                          '& .MuiLinearProgress-bar': {
                            backgroundColor: skill.color,
                            borderRadius: 5,
                          },
                        }}
                      />
                    </motion.div>
                  </Box>
                ))}
              </Paper>
            </Grid>
          </Grid>

          <Box sx={{ mt: 6 }}>
            <Typography variant="h5" gutterBottom align="center" color="primary">
              Languages
            </Typography>
            <Grid container spacing={2} justifyContent="center">
              {['English', 'Hindi', 'Gujarati'].map((language) => (
                <Grid item key={language}>
                  <Paper
                    elevation={2}
                    sx={{
                      px: 3,
                      py: 1,
                      textAlign: 'center',
                      backgroundColor: 'primary.main',
                      color: 'white',
                    }}
                  >
                    <Typography>{language}</Typography>
                  </Paper>
                </Grid>
              ))}
            </Grid>
          </Box>
        </motion.div>
      </Container>
    </Box>
  );
};

export default Skills;
